import React, { Component } from 'react'
import Landing from './components/Landing/Landing'
import Home from './components/Home/Home'
import Register from './components/Register/Register'
import Snackbar from './components/Snackbar/Snackbar'
import RegisterModal from './components/RegisterModal/RegisterModal'
import Login from './components/Login/Login'
import logic from './logic'
import { BrowserRouter as Router, Route, Link, Redirect, withRouter } from "react-router-dom";

logic.url = 'http://localhost:5000/api'

class App extends Component {

  state = { 
    error: 'abc',
    showRegisterModal: false,
    showLoginModal: false
  }

  closeRegisterModalHandler = () => {
    this.setState(() => {
        return ({
            showRegisterModal: !this.state.showRegisterModal
        })
    })
}

  registerClickHandler = () => this.props.history.push('/register')

  loginClickHandler = () => this.props.history.push('/login')

  goBackHandler = () => this.props.history.push('/')

  registerHandler = (name, surname, username, password, email) => {
    logic.registerUser(name, surname, username, password, email)
      .then(() => {
        this.setState({ error: null }, () => this.props.history.push('/login'))
      })
      .catch(err => this.setState({ error: err.message }))
  }

  loginHandler = (username, password) => {
    logic.login(username, password)
      .then(() => this.props.history.push('/'))
      .catch(err => this.setState({ error: err.message }))
  }


  logoutClickHandler = () => {
    logic.logout()
    this.props.history.push('/')
  }

  closeErrorSnackbarHandler = () => {
    this.setState(() => {
      return ({
        error: ''
      })
    })
  }

  render() {
    return (
      <div className='App'>

        {/** Snackbar */}
        {
          this.state.error ?
            <Snackbar
              onCloseSnackbar={this.closeErrorSnackbarHandler}
              message={this.state.error}
              className={'snackbar'}
            />
            : null
        }

        {/** Register Modal */}
        <RegisterModal
          onClose={this.closeRegisterModalHandler}
          show={this.state.showRegisterModal}
          onRegister={this.registerHandler}
        />

        <Route exact path="/" render={() =>
          !logic.loggedIn ?
            <Landing
              onRegisterClick={this.registerClickHandler}
              onLoginClick={this.loginClickHandler} />
            : <Redirect to="/home" />}
        />
        <Route exact path="/home" render={() =>
          logic.loggedIn ?
            <Home
              onRegisterClick={this.registerClickHandler}
              onLogoutClick={this.logoutClickHandler}
              onLoginClick={this.loginClickHandler}
            />
            : <Redirect to="/" />}
        />
        <Route exact path="/register" render={() => !logic.loggedIn ? <Register onRegister={this.registerHandler} onGoBack={this.goBackHandler} /> : <Redirect to="/" />} />
        <Route exact path="/login" render={() => !logic.loggedIn ? <Login onLogin={this.loginHandler} onGoBack={this.goBackHandler} /> : <Redirect to="/" />} />
      </div>
    )
  }
}

export default withRouter(App)
