import React from 'react'
import './Landing.css'
import logic from '../../logic'

import Snackbar from '../Snackbar/Snackbar'
import RegisterModal from '../RegisterModal/RegisterModal'
import LoginModal from '../LoginModal/LoginModal'

class Landing extends React.Component {

    state = {
        error: 'abc',
        showRegisterModal: false,
        showLoginModal: false
    }

    registerClickHandler = () => this.props.history.push('/register')

    loginClickHandler = () => this.props.history.push('/login')

    goBackHandler = () => this.props.history.push('/')

    registerHandler = (name, surname, username, password, email) => {
        logic.registerUser(name, surname, username, password, email)
            .then(() => {
                this.setState({ error: null }, () => this.props.history.push('/login'))
            })
            .catch(err => this.setState({ error: err.message }))
    }

    loginHandler = (username, password) => {
        logic.login(username, password)
            .then(() => this.props.history.push('/'))
            .catch(err => this.setState({ error: err.message }))
    }


    logoutClickHandler = () => {
        logic.logout()
        this.props.history.push('/')
    }

    closeErrorSnackbarHandler = () => {
        this.setState(() => {
            return ({
                error: ''
            })
        })
    }


    render() {

        return (
            <div className='landing-container'>
                <heading className='landing-head'>
                    <h1> Landing Titel </h1>
                </heading>
                <main className='landing-main'>
                    <div className='landing-main__image'><img src='https://images8.alphacoders.com/448/448821.jpg'></img></div>
                    <div className='landing-main__button' onClick={this.props.onLoginClick}> Login </div>
                    <div className='landing-main__button' onClick={this.props.onRegisterClick}> Register </div>
                    <div> Benefits </div>
                </main>
                <footer className='landing-footer'>
                    <div> Second Call to Action </div>
                </footer>
            </div>
        )

    }
}

export default Landing
